@inproceedings{lirolem6925, month= {November}, author= {Thorsten P. Spexard and Marc Hanheide}, note= {Abstract With robotic systems entering our daily life, they have to become more flexible and subsuming a multitude of abilities in one single integrated system. Subsequently an increased extensibility of the robots? system architectures is needed. 
The goal is to facilitate a long-time evolution of the integrated system in-line with the scientific progress on the algorithmic level. In this paper we present an approach developed for an event-driven robot architecture, focussing on the coordination and interplay of new abilities and components. Appropriate timing, sequencing strategies, execution guaranties, and process flow synchronization are taken into account to allow appropriate arbitration and interaction between components as well as between the integrated system and the user. The presented approach features dynamic reconfiguration and global coordination based on simple production rules. These are applied fist time in conjunction with flexible representations in global memory spaces and an event-driven architecture. As a result a highly adaptive robot control compared to alternative approaches is achieved, allowing system modification during runtime even within complex interactive human-robot scenarios}, booktitle= {Conference on Human Centered Robotic Systems}, editor= {B. Gottfried and H. Aghajan}, title= {System integration supporting evolutionary development and design}, publisher= {Springer}, year= {2009}, pages= {1--9}, keywords= {ARRAY(0x7f43493bfce0)}, url= {http://eprints.lincoln.ac.uk/6925/}, abstract= {Abstract With robotic systems entering our daily life, they have to become more flexible and subsuming a multitude of abilities in one single integrated system. Subsequently an increased extensibility of the robots? system architectures is needed. 
The goal is to facilitate a long-time evolution of the integrated system in-line with the scientific progress on the algorithmic level. In this paper we present an approach developed for an event-driven robot architecture, focussing on the coordination and interplay of new abilities and components. Appropriate timing, sequencing strategies, execution guaranties, and process flow synchronization are taken into account to allow appropriate arbitration and interaction between components as well as between the integrated system and the user. The presented approach features dynamic reconfiguration and global coordination based on simple production rules. These are applied fist time in conjunction with flexible representations in global memory spaces and an event-driven architecture. As a result a highly adaptive robot control compared to alternative approaches is achieved, allowing system modification during runtime even within complex interactive human-robot scenarios}}

@inproceedings{lirolem6918, month= {July}, author= {Christian Mertes and Angelika Dierker and Thomas Hermann and Marc Hanheide and Gerhard Sagerer}, note= {Humans naturally use an impressive variety of ways to com-
municate. In this work, we investigate the possibilities of complementing these natural communication channels with articial ones. For this, augmented reality is used as a technique to add synthetic visual and auditory stimuli to people's perception. A system for the mutual display
of the gaze direction of two interactants is presented and its acceptance is shown through a study. Finally, future possibilities of promoting this novel concept of articial communication channels are explored}, booktitle= {Proceedings of the 13th International Conference on Human-Computer Interaction}, editor= {B. Gottfried and H. Aghajan}, title= {Enhancing human cooperation with multimodal augmented reality}, publisher= {Springer}, year= {2009}, pages= {447--451}, keywords= {ARRAY(0x7f43493c0e88)}, url= {http://eprints.lincoln.ac.uk/6918/}, abstract= {Humans naturally use an impressive variety of ways to com-
municate. In this work, we investigate the possibilities of complementing these natural communication channels with articial ones. For this, augmented reality is used as a technique to add synthetic visual and auditory stimuli to people's perception. A system for the mutual display
of the gaze direction of two interactants is presented and its acceptance is shown through a study. Finally, future possibilities of promoting this novel concept of articial communication channels are explored}}

